import { useState } from "react";
import { Layout } from "../../components/Layout";
import { Message } from "../../components/Message";
import StyledButton from '../../components/StyledButton'
import StyledInput from '../../components/StyledInput'

export default function createMessage() {
  const [title, setTitle] = useState<string>('');
  const [body, setBody] = useState<string>('');

  const handleChange = (event: any) => {
    const inputValue = event.target.value;
    event.target.name === 'body' ? setBody(inputValue) : setTitle(inputValue);
  }

  const handleSubmit = (event: any) => {
    event.preventDefault();
    const axios = require('axios');

    axios.post('https://simple-blog-api.crew.red/posts', {
        "title": title,
        "body": body
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  return (
    <Layout>
      <div className="newMessage">
        <h1>Create message</h1>
        <form>
          <b>Body</b>
          <StyledInput onChange={handleChange} name={'body'}></StyledInput>
          <b>Title</b>
          <StyledInput onChange={handleChange} name={'title'}></StyledInput>
          <StyledButton onClick={handleSubmit}>Create</StyledButton>
        </form>
        <Message
          title='Title'
          id='0'
          body='Body'
        />
      </div>
    </Layout>
  )
}
