import { useEffect } from "react";
import { useRouter } from 'next/router';
import { setMessage } from '../../redux/actions/main';
import { connect } from "react-redux";
import { Layout } from "../../components/Layout";
import { TMessageData } from "..";

export type TMessageProps = {
  getMessage: TMessageData[],
  setMessage: (data) => void
}

function MessageInfo(props: TMessageProps) {
  const { getMessage, setMessage } = props;
  const id = useRouter().query.id
  console.log(getMessage)

  useEffect(() => {
    const axios = require('axios');

    axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`)
      .then(function (response: any) {
        setMessage(response.data)
      })
      .catch(function (error: any) {
        console.log(error);
      })
  }, []);


    return (
      <Layout>
        <div className="messageInfo">
          <h1>{`Message №${getMessage.id}`}</h1>
          <h2>{getMessage.title}</h2>
          <h2>{getMessage.body}</h2>
          <div>
            {/* {getMessage.comments.map((elem) => {
              <div>
                <span>{`User №${elem.id}`}</span>
                {elem.body}
              </div>
            })} */}
          </div>
        </div>
      </Layout>
    )
  }

const mapStateToProps = state => ({
  getMessage: state.main.openedMessage
})

const mapDispathToProps = {
  setMessage: setMessage
}

export default connect(mapStateToProps, mapDispathToProps)(MessageInfo)
