import { Layout } from "../../components/Layout";
import StyledButton from '../../components/StyledButton'
import styled, { css } from 'styled-components'

export default function createMessage() {

  const Input = styled.input`
    background: transparent;
    border-radius: 3px;
    border: 2px solid palevioletred;
    color: palevioletred;
    margin: 0.5em 1em;
    padding: 0.25em 1em;

    ${props => props.primary && css`
      background: palevioletred;
      color: white;
    `}
  `;

  const handleSubmit = () => {
    const axios = require('axios');

    axios.post('https://simple-blog-api.crew.red/posts', {
        "title": "In quibusdam tempore odit est dolorem1",
        "body": "Itaque id aut magnam praesentium quia et ea odit et ea voluptas et sapiente quia nihil amet occaecati quia id voluptatem incidunt ea est distinctio odio2"
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  return (
    <Layout>
      <h1>Create massage</h1>
      <form>
        <Input></Input>
        <StyledButton onClick={handleSubmit}>Create</StyledButton>
      </form>
    </Layout>
  )
}
