export default function MessageInfo({resp}) {
  console.log(response, '<><><><><><><><>><><>><>@!@#');
  return <>
    <h1>{response}</h1>
  </>
}

MessageInfo.getInitialProps = async (ctx) => {
  const axios = require('axios');

  const resp = axios.get(`https://simple-blog-api.crew.red/posts/${ctx.query.id}?_embed=comments`)
    .then(function (response: any) {
      console.log(response.data, '<><><><><><><');
      return response.data;
    })
    .catch(function (error: any) {
      console.log(error);
      return {}
    })

  return {resp}
}
