import styled, { css } from 'styled-components'

const StyledButton = styled.button`
    background: transparent;
    border-radius: 8px;
    border: 2px solid palevioletred;
    color: palevioletred;
    margin: 0 1em;
    padding: 0.25em 1em;

    ${props =>
      props.primary &&
      css`
        background: palevioletred;
        color: white;
      `};
  `

export default StyledButton;
