import { useState, useEffect } from "react";
import { Layout } from "../components/Layout";
import { Message } from '../components/Message'
import { connect } from "react-redux";
import { setData } from '../redux/actions/main'
import Link from 'next/link'

export type TMessageData = {
  body: string,
  id: number,
  title: string
}

export type TProps = {
  getData: TMessageData[],
  setData: (data) => void
}

function Home(props: TProps) {
  const { getData, setData } = props;
  console.log(props)

  useEffect(() => {
    const axios = require('axios');

    axios.get('https://simple-blog-api.crew.red/posts')
      .then(function (response: any) {
        setData(response.data);
        return response;
      })
      .catch(function (error: any) {
        console.log(error);
        return {}
      })
  }, []);

  return (
      <Layout>
        <div className="messageGird">
          { getData.map((elem: TMessageData) => {
            return (
              <Link href={'/messageInfo/[id]'} as={`/messageInfo/${elem.id}`}>
                <a>
                  <Message 
                    title={elem.title}
                    body={elem.body}
                    id={elem.id}
                  />
                </a>
              </Link>
            )
          }) }
        </div>
      </Layout>
  )
}

const mapStateToProps = state => ({
  getData: state.main.data
})

const mapDispathToProps = {
  setData: setData
}

Home.getInitialProps = async () => {
    const axios = require('axios');

    axios.get('https://simple-blog-api.crew.red/posts')
      .then(function (response: any) {
        return response;
      })
      .catch(function (error: any) {
        console.log(error);
        return {}
      })
}

export default connect(mapStateToProps, mapDispathToProps)(Home)
