import { Layout } from "../components/Layout";
import StyledButton from '../components/StyledButton'

export default function createMessage() {
  const onSubmit = () => {
    const axios = require('axios');

    axios.post('https://simple-blog-api.crew.red/posts', {
        "title": "In quibusdam tempore odit est dolorem1",
        "body": "Itaque id aut magnam praesentium quia et ea odit et ea voluptas et sapiente quia nihil amet occaecati quia id voluptatem incidunt ea est distinctio odio2"
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  }

  return (
    <Layout>
      <h1>Create massage</h1>
      <StyledButton>Create</StyledButton>
    </Layout>
  )
}
