import { useState } from "react";
import { useRouter } from 'next/router';
import { setMessage } from '../../redux/actions/main';
import { connect } from "react-redux";

function MessageInfo(props) {
  const { getMessage, setMessage } = props;
  console.log(getMessage)
  const id = useRouter().query.id
  const axios = require('axios');

  axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`)
    .then(function (response: any) {
      console.log(response.data)
      setMessage(response.data)
    })
    .catch(function (error: any) {
      console.log(error);
      return {}
    })

  return <>
    <h1></h1>
  </>
}

const mapStateToProps = state => ({
  getMessage: state.main.openedMessage
})

const mapDispathToProps = {
  setMessage: setMessage
}

export default connect(mapStateToProps, mapDispathToProps)(MessageInfo)
