export default function MessageInfo({response}) {
  return <>
    <h1>{response.data.body}</h1>
  </>
}

MessageInfo.getInitialProps = async (ctx) => {
  const axios = require('axios');

  axios.get(`https://simple-blog-api.crew.red/posts/${ctx.query.id}?_embed=comments`)
    .then(function (response: any) {
      console.log(response);
      return response;
    })
    .catch(function (error: any) {
      console.log(error);
      return {}
    })
}
