import styled, { css } from 'styled-components'

const StyledInput = styled.input`
    background: transparent;
    border-radius: 3px;
    border: 2px solid gray;
    color: palevioletred;
    margin: 0.5em 1em;
    padding: 0.25em 1em;
    width: 37vw;

    ${props => props.primary && css`
      background: palevioletred;
      color: white;
    `}
`;

export default StyledInput;
